import React, { Component } from 'react'
import HornedBeast from './HornedBeast'
import Data from './data.json'


class Main extends Component {
    constructor(props){
        super(props);
        this.state={
            width:props.width,
            height:props.height
        }
    }
    updateState=()=>{
        this.setState(
            {
            width: "1000px",
            height:"500px"
            })
        }

    render() {
        return (
            <div>
                {
                    Data.map((b, index) => {
                        return <HornedBeast onClick ={this.updateState()} key={index} clicks={0} title={b.title} imageUrl={b.image_url} description={b.description} />
                    })
                }

            </div>

        )
    }
}

export default Main
