import React from 'react';
import Nav from './Nav';
import Main from './Main';
import Footer from './Footer';
import SelectedBeast from './SelectedBeast';
import Data from './data.json'
import 'bootstrap/dist/css/bootstrap.min.css';

class App extends React.Component{
  constructor(props){
    super(props)
    this.state= {
      show:false,
      data:{},
    }
  }

  handleFunctionToOpen =(Data)=>{
    this.setState({
      show:true,
      data:Data,
    })
  }
  handleFunctionToClose =()=>{
    this.setState({
      show:false,
    })
  }


  render(){
    return(
      <div>
      <Nav/>
      <Main selectModal={this.handleFunctionToOpen}/>
      <SelectedBeast show={this.state.show} handleFunctionToClose={this.handleFunctionToClose} data={this.state.data}/>
      <Footer/>
      </div>
    )
  }
}

export default App;
